using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.InputSystem;public class InputManager : MonoBehaviour{    public static InputManager Instance { get; private set; }    private PlayerInput inputAction;    public InputAction Move, Jump, UseMouse, Grapple;    public InputAction Navigate, Select, SwitchLevel, Pause;    // Start is called before the first frame update    void Awake()    {        if (Instance == null) Instance = this;        else Destroy(this);        if (inputAction == null) inputAction = new PlayerInput();        Move = inputAction.Player.Move;        Jump = inputAction.Player.Jump;                UseMouse = inputAction.Player.UseMouse;                Grapple = inputAction.Player.Grapple;                Navigate = inputAction.MainMenu.Move;        Select = inputAction.MainMenu.Enter;                SwitchLevel = inputAction.MainMenu.SwitchLevel;                Pause = inputAction.MainMenu.Pause;                inputAction.Enable();    }    // Update is called once per frame    void Update()    {            }}